import styled from 'styled-components';

const BigButton = styled.button`
  font-size: 6px;
  background: none;
  border: 0;
  padding: 0;
  margin: 0;
  &:hover {
    color: var(--blue);
    cursor: pointer;
  }
`;

export default function RemoveFromCart({ id }) {
  return (
    <BigButton type="button" title="Remove this Item from Cart">
      Remove Item
    </BigButton>
  );
}
