/* eslint-disable prettier/prettier */

import PhotoSlideshowStyles from './styles/PhotoSlideshowStyles';


// import SlideshowImage1 from './images/slideshowImage1.jpg';

function slideClassList(...classes){
  return classes
  .mySlides
  .fade
}

// function to change an interger from 0 to 1 to 2 back to 0
let imageIndex = 0
function setImageIndex() {
  const maxImageIndex = 2;
  if(imageIndex < maxImageIndex) {
    imageIndex += 1;
  }
  else{
    imageIndex = 0;
  }
}

export default function PhotoSlideshow() {
  // setImageIndex runs every 3 seconds
  setInterval(setImageIndex, 3000);
  if(imageIndex === 0){
    return(
      <PhotoSlideshowStyles>
        <img
            
        />
      </PhotoSlideshowStyles>
    )
  }
  if(imageIndex === 1){
    return(
      <PhotoSlideshowStyles>
      <div className={slideClassList}>
        <div className="slideshowImage">
          <img
            src='/slideshowImage2.jpg'
            alt='people2'
          />
        </div>
      </div>
      </PhotoSlideshowStyles>
    )
  }
  if(imageIndex === 2){
    return(
      <PhotoSlideshowStyles>
      <div className={slideClassList}>
        <div className="slideshowImage">
          <img
            src='/slideshowImage3.jpg'
            alt='people3'
          />
        </div>
      </div>
      </PhotoSlideshowStyles>
    )
  }
}
