import styled from 'styled-components';

const BigButton = styled.button`
  font-size: 3rem;
  line-height: 0.8em;
  background: var(--blue);
  color: white;
  border: 1px black solid;
  border-radius: 10px;
  padding: 5px;
  padding-top: 0;
  margin: 0;
  &:hover {
    color: black;
    cursor: pointer;
  }
`;

export default function RemoveFromCart({ id }) {
  return (
    <BigButton type="button" title="Remove this Item from Cart">
      &times;
    </BigButton>
  );
}
