/* eslint-disable prettier/prettier */
import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';

const SINGLE_ITEM_QUERY = gql`
    query {
        Product(where: { id: "6079b4111e74d44bb01c847c" }) {
            name
            price
        description
        }
}
`;
