import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import Link from 'next/link';
import styled from 'styled-components';
import Table from 'react-bootstrap/Table';
import formatMoney from '../lib/formatMoney';
// import calcTotalPrice from '../lib/calcTotalPrice';
// import DeleteCartItem from './DeleteCartItem';

const BASKET_ITEMS_QUERY = gql`
  query BASKET_ITEMS_QUERY {
    allCartItems {
      quantity
      product {
        id
        name
        price
        description
        photo {
          id
          image {
            publicUrlTransformed
          }
        }
      }
    }
  }
`;

const BasketItemsStyles = styled.div`
  background: white;
  border: 1px solid var(--offWhite);
  box-shadow: var(--bs);
  position: relative;
  display: flex;
  flex-direction: column;
  img {
    width: 100%;
    height: 400px;
    object-fit: cover;
  }
  .item {
    padding: 5px;
  }
`;
let total = 0;
function calcTotal(productTotal) {
  total += productTotal;
  return total;
}

export default function BasketItem({ item }) {
  const { data, error, loading } = useQuery(BASKET_ITEMS_QUERY);
  console.log(data, error, loading);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  return (
    <BasketItemsStyles>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {data.allCartItems.map((item) => (
            <tr>
              <td>
                <Link href={`/product/${item.product.id}`}>
                  <img
                    src={item?.product?.photo?.image?.publicUrlTransformed}
                    alt={item?.product.name}
                  />
                </Link>
              </td>
              <td>{item.product.name}</td>
              <td>{item.product.description}</td>
              <td>{item.quantity}</td>
              <td>
                {formatMoney((total += item.product.price * item.quantity))}
              </td>
            </tr>
          ))}
          <tr>
            <th> </th>
            <th> </th>
            <th> </th>
            <th>Total: </th>
            <th />
          </tr>
        </tbody>
      </Table>
    </BasketItemsStyles>
  );
}
