/* eslint-disable prettier/prettier */
import styled from 'styled-components';

const IndexGrid = styled.div`
  * {
    display: grid;
    grid-gap: 0;
    grid-template-columns: 25% 35% 35% 25%;
    grid-template-rows: 500px 500px 500px 500px;
    padding-top: 1000px;
  }

  .image {
    
    
    background-position: center;
    
    max-height: 500px;
    max-width: 25%;
    
    h1 {
      text-align: center;
      color: rgb(230, 250, 250);
      text-transform: uppercase;
      font-family: Arial, Helvetica, sans-serif;
      font-size: 80px;
      background-color: rgba(0, 0, 15, 0.3);
      font-style: italic;
      margin-top: 12%;
      padding:0;
      margin: 0;
      text-shadow: black 3px 3px;
    }
  }

  #hoodies {
    grid-row: 1;
    grid-column: 1;
    background-image: url(/hoodies.jpg);
    background-size: contain;
    background-repeat: no-repeat;
    position: absolute;
    padding: 0;
    margin: 0;
  }
`;

export default IndexGrid;
