/* eslint-disable prettier/prettier */
import styled from 'styled-components';

const IndexGrid = styled.div`
  * {
    display: grid;
    grid-gap: 0;
    grid-template-columns: 25% 35% 35% 25%;
    grid-template-rows: 500px 500px 500px 500px;
    padding-top: 1000px;
  }

  #hoodies {
    grid-row: 1;
    grid-column: 1;
    background-image: url(/hoodies.jpg);
    background-size: cover;
    background-repeat: no-repeat;
    position: absolute;
    max-height: 100%;
    width: 100%;
    background-position: center;
    img {
      max-height: 100%;
      width: 100%;
    }
  }

  h1 {
    text-align: center;
    color: rgb(230, 250, 250);
    text-transform: uppercase;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 120px;
    background-color: rgba(0, 0, 15, 0.3);
    font-style: italic;
    margin-top: 12%;
  }
`;

export default IndexGrid;
