import styled from 'styled-components';

const BigButton = styled.button`
  font-size: 1.5rem;
  background: none;
  border: 1px black solid;
  padding: 0;
  margin: 0;
  &:hover {
    color: var(--blue);
    cursor: pointer;
  }
`;

export default function RemoveFromCart({ id }) {
  return (
    <BigButton type="button" title="Remove this Item from Cart">
      Remove Item
    </BigButton>
  );
}
