import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import Link from 'next/link';
import styled from 'styled-components';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import formatMoney from '../lib/formatMoney';

const BASKET_ITEMS_QUERY = gql`
  query BASKET_ITEMS_QUERY {
    allCartItems {
      quantity
      product {
        id
        name
        price
        description
        photo {
          id
          image {
            publicUrlTransformed
          }
        }
      }
    }
  }
`;

const BasketItemsStyles = styled.div`
  background: white;
  border: 1px solid var(--offWhite);
  box-shadow: var(--bs);
  position: relative;
  display: flex;
  flex-direction: column;
  img {
    width: 100%;
    height: 400px;
    object-fit: cover;
  }
  .item {
    padding: 5px;
  }
`;

export default function BasketItem({ item }) {
  const { data, error, loading } = useQuery(BASKET_ITEMS_QUERY);
  console.log(data, error, loading);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  return (
    <BasketItemsStyles>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Description</th>
            <th>Size</th>
            <th>Colour</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {data.allCartItems.map((item) => (
            <tr>
              <td>
                <Link href={`/product/${item.product.id}`}>
                  <img
                    src={item?.product?.photo?.image?.publicUrlTransformed}
                    alt={item?.product.name}
                  />
                </Link>
              </td>
              <td>{item.product.name}</td>
              <td>{item.product.description}</td>
              <td>
                <Form>
                  <Form.Group as={Col} controlId="formGridState">
                    <div className="item">
                      <Form.Control as="select" defaultValue=" ">
                        <option>XS</option>
                        <option>S</option>
                        <option>M</option>
                        <option>L</option>
                        <option>XL</option>
                      </Form.Control>
                    </div>
                  </Form.Group>
                </Form>
              </td>
              <td>{/* product.color */} Black</td>
              <td>{/* product.quantity */} 1</td>
              <td>{formatMoney(item?.product.price)}</td>
            </tr>
          ))}
          <tr>
            <th> </th>
            <th> </th>
            <th> </th>
            <th> </th>
            <th> </th>
            <th>Total: </th>
            <th>{/* place total here */}</th>
          </tr>
        </tbody>
      </Table>
    </BasketItemsStyles>
  );
}
